mapper.delete:
DELETE
  FROM POST_COMMENT
 WHERE COMMENT_NO = #{ commentNo }

mapper.insert:
INSERT
  INTO COMMENT_LIKE
(
  COMMENT_NO
, MEMBER_NICKNAME
, STATE_YN
)
VALUES
(
  #{ commentNo }
, #{ nickName }
, 'Y'
)

mapper.update:
UPDATE POST_COMMENT A
   SET A.COMMENT_LIKE = (SELECT COMMENT_LIKE
                           FROM POST_COMMENT
                          WHERE COMMENT_NO = #{ commentNo }
                        ) + 1
 WHERE COMMENT_NO = #{ commentNo }
