mapper.select:
SELECT
       COUNT(*)
  FROM FREE_BOARD


        WRITER = #{ searchValue }


        CONTENTS = #{ searchValue }


        TITLE = #{ searchValue }

mapper.update:
UPDATE
       FREE_BOARD


      , CONTENTS = #{ contents }


      , TITLE = #{ title }

      , UPDATE_TIME = TO_CHAR(SYSDATE, 'yyyy-MM-dd HH24:MI:SS')

 WHERE NO = #{ no }
   AND WRITER = #{ writer }

mapper.delete:
DELETE
  FROM POST_COMMENT
 WHERE COMMENT_NO = #{ commentNo }

mapper.update:
UPDATE
       POST_COMMENT
   SET COMMENT_LIKE = COMMENT_LIKE + 1
 WHERE WRITE_NO = #{ no }
   AND COMMENT_NO = #{ commentNo }

mapper.delete:
UPDATE
       COMMENT_LIKE
   SET STATE_YN = 'N'
 WHERE COMMENT_NO = #{ commentNo }
   AND WRITE_NO = #{ no }
   AND MEMBER_NICKNAME = #{ loginUser }

mapper.update:
UPDATE
       POST_COMMENT
   SET COMMENT_LIKE = COMMENT_LIKE - 1
 WHERE COMMENT_NO = #{ commentNo }
   AND WRITE_NO = #{ no }
